import styled from 'styled-components'

export const Container = styled.div`
background: rgba(52, 69, 225, 0.76);
padding-bottom: 100%;
display: flex;
justify-content:center;
align-items: center;
flex-direction: column;
img{
    width: 500px;
    height: 200px;
    margin-top: 50px;
}

input{
    width: 350px;
    height: 40px;
    color: #707070;
}

button{
    color: #3445E1;
    background: #FFFFFF;
    width: 90px;
    height: 50px;
    margin: 20px;
}

#email{
    margin-top: 100px;
}

#password{
    margin-top: 20px;
}

`
