import React, { useEffect, useState } from 'react'
import Footer from '../../components/Footer'
import Header from '../../components/Header'
import Collapsible from 'react-collapsible'
import api from '../../services/api'
import next from "../../assets/next.png"
import * as S from './styles'

function Transactions(){
    
    const [states, setStates] = useState([])
    const [cities, setCities] = useState([])
    let userData = {}
    let transaction = {
        "gatewayKey": "8353b77a-0d11-4a81-8b34-ac334bed7287",
        "returnUrl": "",
        "foreignOrderId": "",
        "amount": 1.00,
        "installments": 1,
        "cardNumber": "",
        "cardCvv": "",
        "cardExpirationDate": "",
        "cardHolderName": "",
        "cardFlag": "",
        "paymentType": "credit_card",
        "customer": {
        "name": "",
        "document": "",
        "type": "private",
        "email": "",
        "phoneNumbers": [ ],
        "addresses": {
                "street": "teste",
                "number": "",
                "neighborhood": "",
                "city": "",
                "postalCode": "",
                "state": ""}
        },
        "items": [ {
            "name": "item para teste",
            "quantity": 1,
            "unitPrice": 1.00
        } ],
        "boletos": [ ]
    }

    const config = {
        headers: { "Authorization": `bearer ${localStorage.getItem('token')}` }
    }
    
    useEffect(()=>{
        validation()
        loadStates()
    })

    //Carregar Estados
    async function loadStates(){
        await api.get(`/geo/states`)
        .then(response =>{
            setStates(response.data)
        })
        .catch(error =>{
            console.log(error)
        })
    }

    //Carregar Cidades
    async function loadCities(){
        await api.get(`/geo/states/${transaction.customer.addresses.state}/cities`)
        .then(response =>{
            setCities(response.data)
        })
        .catch(error =>{    
            console.log(error)
        })
    }

    //Atualizar cidades com base no estado
    function handleSelectedState(event){
        transaction.customer.addresses.state = `${event.target.value}`
        loadCities()
        let insert = document.getElementById('insertCities')
        insert.innerHTML = cities.map(c => (
            <option value={c._id}>{c.name}</option>
            ))
    }

    function validation(){
        if(localStorage.getItem('token') === null || localStorage.getItem('token') === "undefined"){
            window.location.href = "/"
        }
    }

    async function handleSign(){
        await api.post('/signin', {
            "email": `${localStorage.getItem('email')}`,
            "password": `${document.getElementById("catchPassword").value}`
        })
        .then(response =>{
            userData = JSON.parse(response.request.response)
        })
    }

    return(
        <S.Container>
            <Header />
            <h1>Cadastrar Transação</h1>
            <p id="insertPassword">Primeiro, insira a senha</p>
            <S.Password>
            <input id="catchPassword" type="password" placeholder="Senha"/>
            <img src={next} alt="submitPassword" 
            onClick={handleSign}/>
            </S.Password>
            <S.CustomerData>
            <Collapsible className="collMenu" trigger="Cliente">
            <input className="default" type="text" 
            placeholder="Nome" required
            id="nameCustomer"
            onChange={e => transaction.customer.name = e.target.value}
            ></input>
            <input className="default" type="text" 
            placeholder="CPF ou RG" required
            onChange={e => {transaction.customer.document = e.target.value}}
            />
            <input id="customerEmail" type="text"
            placeholder="E-mail"
            onChange={e => {transaction.customer.email = e.target.value}}
            />
            <input id="phoneNumbers" type="number"
            placeholder="(99) 99999-9999"
            onChange={e => {transaction.customer.phoneNumbers = `${e.target.value}`}}
            />
            <S.Address>
                <input id="street" type="text" 
                placeholder="Endereço" required
                onChange={e => {accountData.address.street = `${e.target.value}`}}
                />
                <input id="number" type="number" 
                placeholder="N°" required
                onChange={e => {accountData.address.number = `${e.target.value}`}}
                />
            </S.Address>
            <S.Complement>
                    <input id="complement" type="text" 
                    placeholder="Complemento" required
                    onChange={e => {accountData.address.complement = `${e.target.value}`}}
                    />
                    <input id="neighborhood" type="text" 
                    placeholder="Bairro" required
                    onChange={e => {accountData.address.neighborhood = `${e.target.value}`}}
                    />
                </S.Complement>
                <S.City>
                    {/* BUSCA POR CIDADES */}
                    <select id="city" name="city" 
                    onChange={e => { 
                        accountData.address.city = `${e.target.value}`
                    }}>
                    <option selected value="">Cidade</option>
                    <div id="insertCities">

                    </div>
                    {
                    cities.map(c => (
                    <option value={c._id}>{c.name}</option>
                    ))
                    }
                    </select>
                    {/* BUSCA POR ESTADOS */}
                    <select id="uf" name="uf" 
                    onLoad={e => { accountData.address.state = `${e.target.value}`}}
                    onChange={handleSelectedState}>
                    <option selected value="">UF</option>
                    {
                    states.map(s => (
                    <option value={s._id}>{s.name}</option>
                    ))
                    }
                    </select>
                    <input id="postalcode" type="number" 
                    placeholder="CEP" required
                    onChange={e => {accountData.address.postalCode = `${e.target.value}`}}
                    />
                </S.City>

                
                </Collapsible>
            </S.CustomerData>
            <img src={next} onClick={() =>{console.log(transaction.customer.name)}}/>
            <Footer />
        </S.Container>
    )

}


export default Transactions
