import React, { useEffect, useState } from 'react'
import Footer from '../../components/Footer'
import Header from '../../components/Header'
import Collapsible from 'react-collapsible'
import api from '../../services/api'
import next from "../../assets/next.png"
import * as S from './styles'

function Transactions(){
    
    const [company, setCompany] = useState([])
    let pass
    let userData = {}
    const config = {
        headers: { "Authorization": `bearer ${localStorage.getItem('token')}` }
    }
    
    useEffect(()=>{
        validation()
    })


    function validation(){
        if(localStorage.getItem('token') === null || localStorage.getItem('token') === "undefined"){
            window.location.href = "/"
        }
    }

    // async function loadUserData(){
    //     await api.get(`/user/`, config)
    //     .then(response =>{
    //         userData = JSON.stringify(response)
    //         console.log(userData)
    //     })
    //     .catch(error =>{
    //         console.log(error)
    //     })
    // }

    async function handleSign(){
        await api.post('/signin', {
            "email": `${localStorage.getItem('email')}`,
            "password": `${document.getElementById("catchPassword").value}`
        })
        .then(response =>{
            userData = JSON.parse(response.request.response)
            console.log(userData.companies[0].integration.gatewayKey)
        })
    }

    return(
        <S.Container>
            <Header />
            <h1>Cadastrar Transação</h1>
            <p id="insertPassword">Primeiro, insira a senha</p>
            <S.Password>
            <input id="catchPassword" type="password" placeholder="Senha"/>
            <img src={next} alt="submitPassword" 
            onClick={handleSign}/>
            </S.Password>
            <Collapsible className="collMenu" trigger="Cliente">
                
            </Collapsible>
            <Footer />
        </S.Container>
    )

}


export default Transactions
