import React, { useState } from 'react'
import * as S from './styles'
import logo from '../../assets/logo.png'
import api from '../../services/api'


function SignIn(){

    const [login, setLogin] = useState()
    const [pass, setPassword] = useState()
    const [data, setData] = useState()

    async function handleLogin(event){
        event.preventDefault()
        await api.post("/signin", {
            email: `${login}`,
            password: `${pass}`
        })
        .then(response =>{
            setData(JSON.parse(`${response.request.response}`))
            localStorage.setItem('token', data.token)
            if(data.companies[0] != "undefined"){
                window.location.href = "/home"
            }
            // return console.log(response.status)
            
        })
        .catch(error =>{
            if(error.status == 401){
                 alert("Dados incorretos, tente novamente!")
            }else{
                alert("Impossível acessar a conta no momento, tente mais tarde!")
            }
        })

    }

    function teste(event){
        event.preventDefault()
        const testeteste = {
            "_id": "5999a9c5-b7c6-4888-afad-d1b083b09389",
            "email": "hml@aquisi.com.br",
            "name": "Carlos Peliciari",
            "phoneNumbers": [
              "19999799045"
            ],
            "companies": [],
            "token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJzdWIiOiI1OTk5YTljNS1iN2M2LTQ4ODgtYWZhZC1kMWIwODNiMDkzODkiLCJuYW1lIjoiQ2FybG9zIFBlbGljaWFyaSIsImVtYWlsIjoiaG1sQGFxdWlzaS5jb20uYnIiLCJpYXQiOjE2MTAwOTMxOTksImV4cCI6MTYxMDE3OTU5OX0.6lKhtIjuKJu8FmIle0daddNnrD2YxCAvKpNxlGqzk28"
          }


        if(testeteste.companies[0] == "null"){
            console.log("null")
        }else if(testeteste.companies == "undefined"){
            console.log("undefined")
        }else{
            console.log(testeteste.companies)
        }
    }

    return(
        <S.Container>
            <img src={logo} alt='logo' />
            <form onSubmit={teste}>

            <input onChange={ e => setLogin(e.target.value)} id="email" 
            type="text" placeholder="Login" required/>

            <input onChange={ e => setPassword(e.target.value)} id="password" 
            type="password" placeholder="Senha" required/>

            <button>Entrar</button>
            </form>
        </S.Container>

    )
}

export default SignIn
