import React, { useEffect, useState } from 'react'
import Footer from '../../components/Footer'
import Header from '../../components/Header'
import Collapsible from 'react-collapsible'
import api from '../../services/api'
import next from "../../assets/next.png"
import * as S from './styles'

function Transactions(){
    
    const [company, setCompany] = useState([])
    const [states, setStates] = useState([])
    const [cities, setCities] = useState([])
    let pass
    let userData = {}
    let accountData ={
        "gatewayKey": "8353b77a-0d11-4a81-8b34-ac334bed7287",
        "returnUrl": "",
        "foreignOrderId": "",
        "amount": 1.00,
        "installments": 1,
        "cardNumber": "",
        "cardCvv": "",
        "cardExpirationDate": "",
        "cardHolderName": "",
        "cardFlag": "",
        "paymentType": "credit_card",
        "customer": {
        "name": "",
        "document": "",
        "type": "private",
        "email": "",
        "phoneNumbers": [ ],
        "addresses": {
                "street": "",
                "number": "",
                "neighborhood": "",
                "city": "",
                "postalCode": "",
                "state": "35"
            }
        },
        "items": [ {
            "name": "",
            "quantity": 1,
            "unitPrice": 1
        } ],
        "boletos": [ ]
        }
    const config = {
        headers: { "Authorization": `bearer ${localStorage.getItem('token')}` }
    }
    
    useEffect(()=>{
        validation()
        loadStates()
    })

    //Carregar Estados
    async function loadStates(){
        await api.get(`/geo/states`)
        .then(response =>{
            setStates(response.data)
        })
        .catch(error =>{
            console.log(error)
        })
    }

    //Carregar Cidades
    async function loadCities(){
        await api.get(`/geo/states/${accountData.customer.address.state}/cities`)
        .then(response =>{
            setCities(response.data)
        })
        .catch(error =>{    
            console.log(error)
        })
    }

    //Atualizar cidades com base no estado
    function handleSelectedState(event){
        accountData.customer.address.state = `${event.target.value}`
        loadCities()
        let insert = document.getElementById('insertCities')
        insert.innerHTML = cities.map(c => (
            <option value={c._id}>{c.name}</option>
            ))
    }

    function validation(){
        if(localStorage.getItem('token') === null || localStorage.getItem('token') === "undefined"){
            window.location.href = "/"
        }
    }

    async function handleSign(){
        await api.post('/signin', {
            "email": `${localStorage.getItem('email')}`,
            "password": `${document.getElementById("catchPassword").value}`
        })
        .then(response =>{
            userData = JSON.parse(response.request.response)
            console.log(accountData.customer.address.state)
        })
    }

    return(
        <S.Container>
            <Header />
            <h1>Cadastrar Transação</h1>
            <p id="insertPassword">Primeiro, insira a senha</p>
            <S.Password>
            <input id="catchPassword" type="password" placeholder="Senha"/>
            <img src={next} alt="submitPassword" 
            onClick={handleSign}/>
            </S.Password>
            <Collapsible className="collMenu" trigger="Cliente">
            <S.Address>
                    <input id="street" type="text" 
                    placeholder="Endereço" required
                    onChange={e => {accountData.customer.address.street = `${e.target.value}`}}
                    />
                    <input id="number" type="number" 
                    placeholder="N°" required
                    onChange={e => {accountData.customer.address.number = `${e.target.value}`}}
                    />
                </S.Address>
                <S.Complement>
                    <input id="neighborhood" type="text" 
                    placeholder="Bairro" required
                    onChange={e => {accountData.customer.address.neighborhood = `${e.target.value}`}}
                    />
                </S.Complement>
                <S.City>
                    {/* BUSCA POR CIDADES */}
                    <select id="city" name="city" 
                    onChange={e => { 
                        accountData.customer.address.city = `${e.target.value}`
                    }}>
                    <option selected value="">Cidade</option>
                    <div id="insertCities">

                    </div>
                    {
                    cities.map(c => (
                    <option value={c._id}>{c.name}</option>
                    ))
                    }
                    </select>
                    {/* BUSCA POR ESTADOS */}
                    <select id="uf" name="uf" 
                    onLoad={e => { accountData.customer[0].address.state = `${e.target.value}`}}
                    onChange={handleSelectedState}>
                    <option selected value="">UF</option>
                    {
                    states.map(s => (
                    <option value={s._id}>{s.name}</option>
                    ))
                    }
                    </select>
                    <input id="postalcode" type="number" 
                    placeholder="CEP" required
                    onChange={e => {accountData.customer.address.postalCode = `${e.target.value}`}}
                    />
                </S.City>
            </Collapsible>
            <Footer />
        </S.Container>
    )

}


export default Transactions
