import React, { useState, useEffect, Component } from 'react'
import * as S from './styles'
import Header from '../../components/Header'
import Collapsible from 'react-collapsible'
import Footer from '../../components/Footer'
import nextIcon from '../../assets/next.png'
import api from '../../services/api'


function CompanyCreate(){

    const [typeCompany, setTypeCompany] = useState('private')
    const [bank, setBank] = useState([])
    const [bankId, setBankId] = useState()

    useEffect(()=>{
        loadBanks()
    },[])

    async function loadBanks() {
        await api.get(`/bank`)
        .then(response => {
            setBank(response.data)
        })
        .catch(error =>{
            console.log(error.data)
        })     
    }

    function handleSelectedCompany(event){
        setTypeCompany(event.target.value)
    }

    function castingSelectedBank(aux) {
        setBankId(aux)
    }

    async function handleSelectedBank(event){
        setBankId(event.target.value, await castingSelectedBank(event.target.value))
        let i;
        for(i = 0; i<=10000; i++){
        setTimeout(function() {
            console.log(bankId)
        }, 1000);}
    }

    

    

    return(
        <S.Container>
            <Header />

                <h1>CRIAÇÃO DE EMPRESA</h1>
            
            <Collapsible className="personal" trigger="Dados Pessoais">
            <S.PersonalArea>
                
                <input className="default" type="text" 
                placeholder="Nome / Razão Social" required/>
                <input className="default" type="text" 
                placeholder="CPF / CNPJ" required/>
                <select id="types" name="types" onChange={handleSelectedCompany}>
                <option selected value="legal">Pessoa Física</option>
                <option value="private">Pessoa Jurídica</option>
                </select>
                <S.Address>
                <input id="address" type="text" 
                placeholder="Endereço" required/>
                <input id="number" type="text" 
                placeholder="N°" required/>
                </S.Address>
                <S.Complement>
                <input id="complement" type="text" 
                placeholder="Complemento" required/>
                <input id="neighborhood" type="text" 
                placeholder="Bairro" required/>
                </S.Complement>
                <S.City>
                <input id="city" type="text" 
                placeholder="Cidade" required/>
                <input id="uf" type="text" 
                placeholder="UF" required/>
                <input id="postalcode" type="text" 
                placeholder="CEP" required/>
                </S.City>
            </S.PersonalArea>
            </Collapsible>

            <S.BankArea>
                <select id="bank" name="bank" onLoad={(event) => handleSelectedBank(event)} onChange={(event) => handleSelectedBank(event)}>
                    {
                    bank.map(b => (
                    <option value={b._id}>{b.name}</option>
                    ))}
                </select>
            </S.BankArea>
                    
                <S.Next>
                <a>
                <img src={nextIcon} alt="next" />
                </a>
                </S.Next>
            
            <Footer />
        </S.Container>
    )
}

export default CompanyCreate
