import styled from 'styled-components'

export const Container = styled.div`
background: rgba(52, 69, 225, 0.76);
padding-bottom: 100%;
display: flex;
text-align: center;
justify-content: center;
align-items: center;
flex-direction: column;
img{
    width: 500px;
    height: 200px;
    margin-top: 50px;
    align-items: center;
    
}
`

export const Login = styled.div`

input{
    width: 350px;
    height: 40px;
    color: #707070;
    padding-left: 10px;
    align-items: center;
    justify-content: center;
    display: flex;
    
}

button{
    color: #3445E1;
    background: #FFFFFF;
    font-weight: bold;
    width: 200px;
    height: 50px;
    margin: 20px;
    border: none;
    border-radius: 10px;
    position: relative;
    align-items: center;
    justify-content: center;
    display: flex;
    flex-direction: column;
}

#email{
    margin-top: 100px;
}

#password{
    margin-top: 20px;
}

`
