import React, { useEffect, useState } from 'react'
import Footer from '../../components/Footer'
import Header from '../../components/Header'
import Collapsible from 'react-collapsible'
import api from '../../services/api'
import next from "../../assets/next.png"
import * as S from './styles'

function Transactions(){
    
    const [company, setCompany] = useState([])
    let pass
    let userData = {}
    const config = {
        headers: { "Authorization": `bearer ${localStorage.getItem('token')}` }
    }
    
    useEffect(()=>{
        validation()
    })


    function validation(){
        if(localStorage.getItem('token') === null || localStorage.getItem('token') === "undefined"){
            window.location.href = "/"
        }
    }

    // async function loadUserData(){
    //     await api.get(`/user/`, config)
    //     .then(response =>{
    //         userData = JSON.stringify(response)
    //         console.log(userData)
    //     })
    //     .catch(error =>{
    //         console.log(error)
    //     })
    // }

    async function handleCompany(){
        let insert = document.getElementById("insertCompany")
        let aux, i, userCompanies = userData.companies.length
        console.log(userData.companies.length)
        for (i = 0; i<userCompanies; i++){
            aux = userData.companies[i].integration.gatewayKey
            if(aux !== null && aux !== "undefined"){
                insert.innerHTML = <option value={aux}>{userData.companies[i].name}</option>
            }
        }
        
        
     }

    async function handleSign(){
        await api.post('/signin', {
            "email": `${localStorage.getItem('email')}`,
            "password": `${document.getElementById("catchPassword").value}`
        })
        .then(response =>{
            userData = JSON.parse(response.request.response)
            handleCompany()
        })
    }

    return(
        <S.Container>
            <Header />
            <h1>Cadastrar Transação</h1>
            <p id="insertPassword">Primeiro, insira a senha</p>
            <input id="catchPassword" placeholder="Senha"/>
            <img src={next} alt="submitPassword" 
            onClick={handleSign}/>
            <Collapsible trigger="Empresa">
                {/* <select value="" onLoad={handleCompany}> */}
                <select value="none" >
                <option selected value="none">Selecionar Empresa</option>
                <div id="insertCompany">
                {<option 
                value={aux}>
                    {userData.companies[i].name}</option>}
                </div>
                {/* {
                company.map(c =>(
                    <option value={c.companies.gatewayKey}>{c.companies.name}</option>
                ))
                } */}
                </select>
            </Collapsible>
            <Footer />
        </S.Container>
    )

}


export default Transactions
