import React, { useEffect, useState } from 'react'
import Footer from '../../components/Footer'
import Header from '../../components/Header'
import Collapsible from 'react-collapsible'
import api from '../../services/api'
import * as S from './styles'

function Transactions(){
    
    const [company, setCompany] = useState([])
    let userData = {}
    const config = {
        headers: { "Authorization": `bearer ${localStorage.getItem('token')}` }
    }
    
    useEffect(()=>{
        validation()
        loadUserData()
    },[])


    function validation(){
        if(localStorage.getItem('token') === null || localStorage.getItem('token') === "undefined"){
            window.location.href = "/"
        }
    }

    async function loadUserData(){
        await api.get(`/user/`, config)
        .then(response =>{
            userData = JSON.stringify(response.request.response)
            console.log(userData)
        })
        .catch(error =>{
            console.log(error)
        })
    }

    async function handleCompany(event){
        loadUserData()
        let i
        let insert = document.getElementById("insertCompany")
        for(i = 0; i<30; i++){
            insert.innerHTML = <option
            value={userData.companies[i].gatewayKey}>
                {userData.companies[i].name}</option>
        }
        
        
    }

    return(
        <S.Container>
            <Header />
            <h1>Cadastrar Transação</h1>
            <Collapsible trigger="Empresa">
                <select value="" onLoad={handleCompany}>
                <option selected value="">Selecionar Empresa</option>
                <div id="insertCompany">

                </div>
                {
                company.map(c =>(
                    <option value={c.companies.gatewayKey}>{c.companies.name}</option>
                ))
                }
                </select>
            </Collapsible>
            <Footer />
        </S.Container>
    )

}


export default Transactions
