import styled from 'styled-components'

export const Container = styled.div`
    width: 100%;
    display: flex;
    flex-direction: column;


`

export const CreationArea = styled.div`
    width: 100%;
    color: #893DCC;
    display: flex;
    flex-direction: column;
    text-align: center;
    align-items: center;
    justify-content: center;

    h1{
        font-weight: bold;
    }

    input, select, options{
        height: 30px;
        border-color: rgba(52, 69, 255, 0.76);
        border-radius: 10px;
        padding-left: 5px;
        margin: 5px 0px;
    }

    .default, #types{
        width: 400px;
    }

    .types{
        width: 400px;
    }

`
