import styled from 'styled-components'

export const Container = styled.div`
background: rgba(52, 69, 225, 0.76);
padding-bottom: 100%;
display: flex;
justify-content:center;
align-items: center;
flex-direction: column;
img{
    width: 500px;
    height: 200px;
    margin-top: 50px;
}

input{
    width: 150px;
    height: 50px;
}

`
